let device = msg.device;
const debug = flow.get("$parent.g_debug");

switch (msg.statusCode) {
    //////////////////////////////////////////////////    
    // Case 200 : "Success" > Stops here OR continue to read Downlink Objects. 
    // Case 200 : "Object does not exist" > Create Objects
    //////////////////////////////////////////////////
    case 200:
        if (msg.payload.includes("Unknown Object")) {
            debug(device, "creation", `${device.identity.deviceName} : Some BACnet objects don't exist`);
            return [{ device: device }, null];       // Create Downlink Objects
        }
        else {
            switch (env.get("dataDirection")) {
                case "uplink":
                    debug(device, "up", `${device.identity.deviceName} (RestAPI) : Write Uplink Objects`);

                    const dataDirection = Object.values(device.bacnet.objects).map(obj => obj.dataDirection);

                    if (dataDirection.some(direction => { return direction === "downlink" })) {
                        return [null, { device: device }];    // Continue to read downlink Objects
                    }
                    else {
                        debug(device, "txTime", `${device.identity.deviceName} (${device.controller.protocol}) : TX time = ${Date.now() - device.transmitTime} ms`);
                        return [null, null];                 // Stops here
                    }
                    break;
                case "downlink":
                    debug(device, "down", `${device.identity.deviceName} (RestAPI) : Read Downlink Objects`);
                    return [null, { device: device, payload: JSON.parse(msg.payload) }];
                    break;
                default:

            }

        }

    case 400:
        node.error("Error : Bad HTTP Request");
        if (msg.payload.includes("write-access-denied")) {
            node.error("Error : Trying to write a Read Only object (analogInput)");
        }
        return [null, null];

    case 401:
        node.error("Error : Can't connect to controller : Authorization error");
        return [null, null];

    case 500:
        node.error("Error : Server Error 500");
        return [null, null];

    case 404:
        node.error("Error : 404");
        return [null, null];

    case "ETIMEDOUT":
        node.error("Error : Can't connect to controller : TimeOut");
        return [null, null];

    case "UNABLE_TO_VERIFY_LEAF_SIGNATURE":
        node.error("Error : You forgot to enable the TLS config in your HTTP node");
        return [null, null];

}

