/* This function stores the downlink data from the controller */

let device = msg.device;
let bacnetObjects = device.bacnet.objects;

// For InfluxDB support
device.influxdb.source = "downlink";

switch (device.controller.model) {
    case "distechControlsV2":
        let donwlinkObjects = msg.payload;

        for (let i = 0; i < donwlinkObjects.results.length; i++) {
            Object.values(bacnetObjects).forEach(obj => {
                if (donwlinkObjects.results[i].type == obj.objectType && donwlinkObjects.results[i].instance == obj.instanceNum) {
                    if (obj.objectType == "analogValue") obj.value = Number(donwlinkObjects.results[i].value);
                    if (obj.objectType == "binaryValue") obj.value = donwlinkObjects.results[i].value;
                }
            });
        }

        return {
            device: device
        };



    ///////////////////////////////////////////////////////////
    ////// XXXXX Controller
