///////////////////////////////////////////////////////////
////// This part is device dependant
////// The configuration depends on the downlink strategy
///////////////////////////////////////////////////////////

let device = msg.device;
if (device.lorawan.hasOwnProperty("defaultValuesForDownlink")){
    var staticDownlinkObjects = device.lorawan.defaultValuesForDownlink
}
let bacnetObjects = device.bacnet.objects;
const debug = flow.get("g_debug");
let downlinkLowPriorityObject = 0;
let previousValues = RED.util.cloneMessage(flow.get("g_previousValues"));
let previousBacnetObject = previousValues[device.identity.deviceName].bacnet.objects;
let payload={};

function downlinkPayloadCreation(downlinkObjectToSend) {
    //Creation of the downlink payload
    for (let obj in bacnetObjects){
        if (bacnetObjects[obj].dataDirection === "downlink" && bacnetObjects[obj].downlinkPort == bacnetObjects[downlinkObjectToSend].downlinkPort){
            let temp = "{ \"" + obj + "\" : " + JSON.stringify(bacnetObjects[obj].value) + " }";                        
            payload = { ...payload, ...JSON.parse(temp) }
        }
    }
    // Chek if there are other values to add to the payload
    if (device.lorawan.hasOwnProperty("defaultValuesForDownlink") ){
        if (device.lorawan.defaultValuesForDownlink.hasOwnProperty("fPort_" + bacnetObjects[downlinkObjectToSend].downlinkPort)){
            for (let obj in staticDownlinkObjects["fPort_"+ bacnetObjects[downlinkObjectToSend].downlinkPort]){
                    let temp = "{ \"" + obj + "\" : " + JSON.stringify(staticDownlinkObjects["fPort_"+ bacnetObjects[downlinkObjectToSend].downlinkPort][obj]) + " }";                        
                    payload = { ...payload, ...JSON.parse(temp) }

            }
        }
    }
    msg ={
        "payload" : payload,
        "device" :device
    }
    msg.device.lorawan.downlinkPort = bacnetObjects[downlinkObjectToSend].downlinkPort

}

for (let object in bacnetObjects) {

    if (bacnetObjects[object].dataDirection === "downlink") {

        switch (bacnetObjects[object].downlinkPortPriority) {
            case "high":
                switch (bacnetObjects[object].downlinkStrategie) {
                    case "onChangeOfThisValue":
                        if (bacnetObjects[object].value != previousBacnetObject[object].value) {
                            node.status({ fill: "yellow", shape: "dot", text: "Downlink high priority COV" });
                            debug(device, "down", `${device.identity.deviceName} (${device.controller.protocol}) : Downlink scheduled : Previous value ${previousBacnetObject[object].value}  != ${object} ${bacnetObjects[object].value}`);
                            //Creation of the downlink payload
                            downlinkPayloadCreation(object)
                            return msg
                        }
                        break;
                    case "onChangeOfThisValueWithinRange":
                        if (bacnetObjects[object].value != previousBacnetObject[object].value && bacnetObjects[object].value <= bacnetObjects[object].range[1] && bacnetObjects[object].value >= bacnetObjects[object].range[0]) {
                            node.status({ fill: "yellow", shape: "dot", text: "Downlink high priority COVWR" });
                            debug(device, "down", `${device.identity.deviceName} (${device.controller.protocol}) : Downlink scheduled : Previous value ${previousBacnetObject[object].value}  != ${object} ${bacnetObjects[object].value}`);
                            //Creation of the downlink payload
                            downlinkPayloadCreation(object)
                            return msg
                        }
                        break;
                    case "compareToUplinkObjectWithinRange":
                        if (bacnetObjects[object].value != bacnetObjects[bacnetObjects[object].uplinkObjectToCompareWith].value && bacnetObjects[object].value <= bacnetObjects[object].range[1] && bacnetObjects[object].value >= bacnetObjects[object].range[0]) {
                            node.status({ fill: "yellow", shape: "dot", text: "Downlink high priority CUVWR" });
                            debug(device, "down", `${device.identity.deviceName} (${device.controller.protocol}) : Downlink scheduled : ${bacnetObjects[object].uplinkObjectToCompareWith} ${bacnetObjects[bacnetObjects[object].uplinkObjectToCompareWith].value}  != ${object} ${bacnetObjects[object].value}`);
                            //Creation of the downlink payload
                            downlinkPayloadCreation(object)
                            return msg
                        }
                        break;
                    case "compareToUplinkObject":
                        if (bacnetObjects[object].value != bacnetObjects[bacnetObjects[object].uplinkObjectToCompareWith].value) {
                            node.status({ fill: "yellow", shape: "dot", text: "Downlink high priority CUV" });
                            debug(device, "down", `${device.identity.deviceName} (${device.controller.protocol}) : Downlink scheduled : ${bacnetObjects[object].uplinkObjectToCompareWith} ${bacnetObjects[bacnetObjects[object].uplinkObjectToCompareWith].value}  != ${object} ${bacnetObjects[object].value}`);
                            //Creation of the downlink payload
                            downlinkPayloadCreation(object)
                            return msg
                        }
                        break;
                    default:

                }

                break;
            case "low":
                //In case of low priority downlink the object name is kept till the end of the for loop 
                // to be sur that there is not any high priority downlink to send 
                switch (bacnetObjects[object].downlinkStrategie) {
                    case "onChangeOfThisValue":
                        if (bacnetObjects[object].value != previousBacnetObject[object].value) {
                            downlinkLowPriorityObject = object;
                        }
                        break;
                    case "onChangeOfThisValueWithinRange":
                        if (bacnetObjects[object].value != previousBacnetObject[object].value && bacnetObjects[object].value <= bacnetObjects[object].range[1] && bacnetObjects[object].value >= bacnetObjects[object].range[0]) {
                            downlinkLowPriorityObject = object;
                        }
                        break;
                    case "compareToUplinkObjectWithinRange":
                        if (bacnetObjects[object].value != bacnetObjects[bacnetObjects[object].uplinkObjectToCompareWith].value && bacnetObjects[object].value <= bacnetObjects[object].range[1] && bacnetObjects[object].value >= bacnetObjects[object].range[0]) {
                            downlinkLowPriorityObject = object;
                        }
                        break;
                    case "compareToUplinkObject":
                        if (bacnetObjects[object].value != bacnetObjects[bacnetObjects[object].uplinkObjectToCompareWith].value) {
                            downlinkLowPriorityObject = object;
                        }
                        break;
                    default:

                }
                break;
            default:

        }
    }
}
if (downlinkLowPriorityObject != 0) {
    node.status({ fill: "yellow", shape: "dot", text: "Downlink low priority" });
    switch (bacnetObjects[downlinkLowPriorityObject].downlinkStrategie) {
        case "onChangeOfThisValue":
            debug(device, "down", `${device.identity.deviceName} (${device.controller.protocol}) : Downlink scheduled : Previous value ${previousBacnetObject[downlinkLowPriorityObject].value}  != ${downlinkLowPriorityObject} ${bacnetObjects[downlinkLowPriorityObject].value}`);
            break;
        case "onChangeOfThisValueWithinRange":
            debug(device, "down", `${device.identity.deviceName} (${device.controller.protocol}) : Downlink scheduled : Previous value ${previousBacnetObject[downlinkLowPriorityObject].value}  != ${downlinkLowPriorityObject} ${bacnetObjects[downlinkLowPriorityObject].value}`);
            break;
        case "compareToUplinkObjectWithinRange":
            debug(device, "down", `${device.identity.deviceName} (${device.controller.protocol}) : Downlink scheduled : ${bacnetObjects[downlinkLowPriorityObject].uplinkObjectToCompareWith} ${bacnetObjects[bacnetObjects[downlinkLowPriorityObject].uplinkObjectToCompareWith].value}  != ${downlinkLowPriorityObject} ${bacnetObjects[downlinkLowPriorityObject].value}`);
            break;
        case "compareToUplinkObject":
            debug(device, "down", `${device.identity.deviceName} (${device.controller.protocol}) : Downlink scheduled : ${bacnetObjects[downlinkLowPriorityObject].uplinkObjectToCompareWith} ${bacnetObjects[bacnetObjects[downlinkLowPriorityObject].uplinkObjectToCompareWith].value}  != ${downlinkLowPriorityObject} ${bacnetObjects[downlinkLowPriorityObject].value}`);
            break;
        default:

    }
    //Creation of the dowlink payload
    downlinkPayloadCreation(downlinkLowPriorityObject)
    return msg            
}else{

    node.status({fill: "green", shape: "dot" ,text: "No downlink"});
    return null;
}

let device = msg.device;
let downlinkJson = {};
const debug = flow.get('g_debug');

// Modify the downlink object according to the lorawanPayloadName.

for (let object in msg.payload) {
    // Don't do any changes if lorawanPayloadName is the same as the BACnet Object name.
    if (!Object.keys(device.bacnet.objects).some(element => element == device.bacnet.objects[object].lorawanPayloadName)) {
